/* Import plugin specific language pack */
 tinyMCE.importPluginLanguagePack('advlink','en,de,sv,zh_cn,cs,fa,fr_ca,fr,pl,pt_br,nl');function TinyMCE_advlink_getControlHTML(control_name){switch(control_name){case "link":return '<img id="{$editor_id}_advimage" src="{$themeurl}/images/link.gif" title="{$lang_link_desc}" width="20" height="20" class="mceButtonNormal" onmouseover="tinyMCE.switchClass(this,\'mceButtonOver\');" onmouseout="tinyMCE.restoreClass(this);" onmousedown="tinyMCE.restoreAndSwitchClass(this,\'mceButtonDown\');tinyMCE.execInstanceCommand(\'{$editor_id}\',\'mceAdvLink\');" />';}return "";}function TinyMCE_advlink_execCommand(editor_id,element,command,user_interface,value){switch(command){case "mceAdvLink":var template=new Array();template['file']='../../plugins/advlink/link.htm';template['width']=480;template['height']=430-(tinyMCE.isMSIE?30:0);template['width']+=tinyMCE.getLang('lang_insert_link_delta_width',0);template['height']+=tinyMCE.getLang('lang_insert_link_delta_height',0);tinyMCE.openWindow(template,{editor_id:editor_id});return true;}return false;}